import React, { useState } from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = (props) => {
  function yearFilterChangeHandler(event) {
    const [pickedYear, setPickedYear] = useState("");
    setPickedYear(event.target.value);
  };

  props.onSaveYearFilterData(pickedYear);

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select>
          <option value="2022" onCh ={yearFilterChangeHandler}>
            2022
          </option>
          <option value="2021" onChange={yearFilterChangeHandler}>
            2021
          </option>
          <option value="2020" onChange={yearFilterChangeHandler}>
            2020
          </option>
          <option value="2019" onChange={yearFilterChangeHandler}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
